void setup()
{
  Serial.begin(115200);

  displaySetup();

  dma_display->clearScreen();
  dma_display->setTextColor(myCYAN);
  dma_display->setCursor(2, 0);
  dma_display->print("Pixel");
  dma_display->setTextColor(myMAGENTA);
  dma_display->setCursor(2, 8);
  dma_display->print("Time");

  delay(3000);
}
union single_double
{
  uint8_t two[2];
  uint16_t one;
} this_single_double;

// This draws the weather icons
void draw_weather_icon(uint8_t icon)
{
  if (icon > 10)
    icon = 10;
  for (int yy = 0; yy < 10; yy++)
  {
    for (int xx = 0; xx < 10; xx++)
    {
      uint16_t byte_pos = (xx + icon * 10) * 2 + yy * 220;
      this_single_double.two[1] = weather_icons[byte_pos];
      this_single_double.two[0] = weather_icons[byte_pos + 1];
      dma_display->drawPixel(1 + xx, yy, this_single_double.one);
    }
  }
}

unsigned long last_draw = 0;
void scroll_text(uint8_t ypos, unsigned long scroll_delay, String text, uint8_t colorR, uint8_t colorG, uint8_t colorB)
{
  uint16_t text_length = text.length();
  dma_display->setTextWrap(false); // we don't wrap text so it scrolls nicely
  dma_display->setTextSize(1);
  dma_display->setRotation(0);
  dma_display->setTextColor(dma_display->color565(colorR, colorG, colorB));

  // Asuming 5 pixel average character width
  for (int xpos = matrix_width; xpos > -(matrix_width + text_length * 5); xpos--)
  {
    dma_display->setTextColor(dma_display->color565(colorR, colorG, colorB));
    dma_display->clearScreen();
    dma_display->setCursor(xpos, ypos);
    dma_display->println(text);
    delay(scroll_delay);
    yield();

    // This might smooth the transition a bit if we go slow
    // dma_display->setTextColor(dma_display->color565(colorR/4,colorG/4,colorB/4));
    // dma_display->setCursor(xpos-1,ypos);
    // dma_display->println(text);

    delay(scroll_delay / 5);
    yield();
  }
}

uint8_t icon_index = 0;
void loop()
{
  scroll_text(1, 50, "Welcome to MatrixPanel!", 96, 96, 250);
  dma_display->clearScreen();

  draw_weather_icon(icon_index);
  icon_index++;
  if (icon_index > 10)
    icon_index = 0;

  for (int xx = 0; xx < 16; xx++)
  {
    dma_display->drawLine(xx + 16, 0, xx + 16, 5, dma_display->color565(xx * 16, 0, 0));
    dma_display->drawLine(xx + 16, 6, xx + 16, 10, dma_display->color565(0, xx * 16, 0));
    dma_display->drawLine(xx + 16, 11, xx + 16, 15, dma_display->color565(0, 0, xx * 16));
  }
  delay(1000);
  for (uint8_t dimm = 255; dimm > 0; dimm--)
  {
    dma_display->setBrightness(dimm);
    delay(5);
  }
  for (uint8_t dimm = 0; dimm < 255; dimm++)
  {
    dma_display->setBrightness(dimm);
    delay(5);
  }
}
